import gulp from 'gulp';
import gulpLoadPlugins from 'gulp-load-plugins';
import browserSync from 'browser-sync';
import del from 'del';

const $ = gulpLoadPlugins();
const reload = browserSync.reload;

const compileStyles = () => {
    const AUTOPREFIXER_BROWSERS = [
        'ie >= 10',
        'ie_mob >= 10',
        'ff >= 30',
        'chrome >= 34',
        'safari >= 6',
        'opera >= 23',
        'ios >= 6',
        'android >= 4.4',
        'bb >= 10',
        'iOS 7'
    ];

    return gulp.src([
        'src/scss/styles.scss',
        ])
        .pipe($.sass())
        .pipe(gulp.src([
        'src/scss/helpers/*.css',
        'src/scss/*.css']))
        .pipe($.concat('main.css'))
        .pipe($.autoprefixer(AUTOPREFIXER_BROWSERS))
        .pipe($.cssnano())
        .pipe(gulp.dest('dist/css'))
        .pipe(gulp.dest('src/css'))
        .pipe($.size({title: 'Styles'}))

}
const compileScripts = () => {
    const babelOptions = {
        presets: ['@babel/preset-env']
    };
    return gulp.src([
        'vendor/*.js', 'src/js/*.js'])
        .pipe($.babel(babelOptions))
        .pipe(gulp.dest('dist/js'))
        .pipe($.size({title: 'Scripts'}))
}

const optimizeImages = () => {
    return gulp.src('src/img/*')
        .pipe($.cache($.imagemin({
            progressive: true,
            interlaced: true
        })))
        .pipe(gulp.dest('dist/img'))
        .pipe($.size({title: 'Images'}))
}

const watchStyles = () => {
    gulp.watch(['src/scss/*.{scss, css}', 'src/scss/**/*.{scss, css}']).on('change', function(event) {
        gulp.series(compileStyles, reload)
    });
}

const watchScripts = () => {
    gulp.watch('src/js/*.js').on('change', function(event) {
        gulp.series(compileScripts, reload)
    });
}

const startServer = () => {
    browserSync({
        notify: false,
        logPrefix: 'WSK',
        scrollElementMapping: ['main', '.mdl-layout'],
        server: './src',
        watch: true,
        port: 3000
    });
}

const clear = (done) => {
    del.sync(['dist/*'])
    done()
}

const copy = (done) => {
    gulp.src(['./src/*.html']).pipe(gulp.dest('dist'));
    gulp.src(['./src/*.png', './src/*.png']).pipe(gulp.dest('dist/'));
    gulp.src(['./src/fonts/*']).pipe(gulp.dest('dist/fonts'));
    gulp.src(['./src/js/*']).pipe(gulp.dest('dist/js'));
    done()
}

const compile = gulp.series(compileStyles, compileScripts)
compile.description = 'compile all sources'

const serve = gulp.series(compile, startServer)
serve.description = 'serve compiled source on local server at port 3000'

const watch = gulp.parallel(watchScripts, watchStyles)
watch.description = 'watch for changes to all source'

const build = gulp.series(clear, compile, optimizeImages, copy)
build.description = 'craft build'

const defaultTasks = gulp.series(serve, watch)


export {
  compile,
  compileScripts,
  compileStyles,
  serve,
  copy,
  optimizeImages,
  watch,
  build,
  clear
}

export default defaultTasks

